import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Apply = ({ user }) => {
  const { jobId } = useParams();
  const [job, setJob] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchJob = async () => {
      try {
        const res = await axios.get(`/api/jobs/${jobId}`);
        setJob(res.data);
      } catch (err) {
        setError('Job not found');
      }
    };
    fetchJob();
  }, [jobId]);

  if (!user || user.role !== 'applicant') {
    navigate('/');
    return null;
  }

  const handleApply = async () => {
    try {
      await axios.post('/api/applications', { jobId }, {
        headers: { Authorization: `Bearer ${user.token}` }
      });
      setSuccess('Application submitted!');
      setTimeout(() => navigate('/my-applications'), 2000);
    } catch (err) {
      setError('Failed to apply. Maybe already applied?');
    }
  };

  if (error) return <p className="text-red-500">{error}</p>;
  if (!job) return <p>Loading...</p>;

  return (
    <div className="max-w-md mx-auto">
      <h1 className="text-3xl font-bold mb-6 text-primary">Apply for {job.title}</h1>
      <p className="mb-4">{job.description}</p>
      {success && <p className="text-green-500 mb-4">{success}</p>}
      {error && <p className="text-red-500 mb-4">{error}</p>}
      <button onClick={handleApply} className="w-full bg-secondary text-white py-2 rounded hover:bg-green-700">
        Submit Application
      </button>
    </div>
  );
};

export default Apply;
