import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import axios from 'axios';
import JobCard from '../components/JobCard';

const LandingPage = ({ user }) => {
  const [jobs, setJobs] = useState([]);
  const [filteredJobs, setFilteredJobs] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [category, setCategory] = useState('all');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const res = await axios.get('/api/jobs');
        // Simulate categories for demo (replace with real data from backend if available)
        const jobsWithCategories = res.data.map((job, index) => ({
          ...job,
          category: index % 2 === 0 ? 'Software' : 'Design', // Mock categories
          isFeatured: index === 0, // First job is featured
        }));
        setJobs(jobsWithCategories);
        setFilteredJobs(jobsWithCategories);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchJobs();
  }, []);

  // Handle search and filter
  useEffect(() => {
    const results = jobs.filter(job =>
      (job.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
       job.description.toLowerCase().includes(searchTerm.toLowerCase())) &&
      (category === 'all' || job.category === category)
    );
    setFilteredJobs(results);
  }, [searchTerm, category, jobs]);

  if (loading) return (
    <div className="flex justify-center items-center h-64">
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary"></div>
    </div>
  );

  return (
    <div className="space-y-12">
      {/* Hero Section */}
      <motion.section
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="relative text-center py-16 bg-cover bg-center rounded-lg shadow-xl overflow-hidden"
        style={{
          backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url('https://images.unsplash.com/photo-1516321310762-479437144403?ixlib=rb-4.0.3&auto=format&fit=crop&w=1350&q=80')`,
        }}
      >
        <div className="container mx-auto px-4">
          <h1 className="text-4xl md:text-5xl font-bold text-white mb-4">Find Your Dream Job at TalentHub</h1>
          <p className="text-lg md:text-xl text-gray-200 mb-6">
            Connect with top employers or discover the best talent for your team.
          </p>
          {user ? (
            <p className="text-sm text-gray-300">Logged in as {user.name} ({user.role})</p>
          ) : (
            <div className="space-x-4">
              <motion.a
                href="/register"
                whileHover={{ scale: 1.05 }}
                className="inline-block bg-secondary text-white px-8 py-3 rounded-full font-semibold hover:bg-green-700 transition"
              >
                Get Started
              </motion.a>
              <motion.a
                href="/login"
                whileHover={{ scale: 1.05 }}
                className="inline-block bg-white text-primary px-8 py-3 rounded-full font-semibold hover:bg-gray-100 transition"
              >
                Log In
              </motion.a>
            </div>
          )}
        </div>
      </motion.section>

      {/* Search and Filter */}
      <section className="flex flex-col sm:flex-row justify-center items-center gap-4">
        <input
          type="text"
          placeholder="Search jobs by title or description..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full max-w-md p-3 border border-gray-300 rounded-full shadow-sm focus:outline-none focus:ring-2 focus:ring-primary transition"
        />
        <select
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          className="w-full max-w-xs p-3 border border-gray-300 rounded-full shadow-sm focus:outline-none focus:ring-2 focus:ring-primary"
        >
          <option value="all">All Categories</option>
          <option value="Software">Software</option>
          <option value="Design">Design</option>
        </select>
      </section>

      {/* Job Listings */}
      <section>
        <h2 className="text-3xl font-bold mb-8 text-primary text-center">Explore Job Opportunities</h2>
        <AnimatePresence>
          {filteredJobs.length === 0 ? (
            <motion.p
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="text-center text-gray-500"
            >
              No jobs match your search.
            </motion.p>
          ) : (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredJobs.map(job => (
                <motion.div
                  key={job.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5 }}
                >
                  <JobCard job={job} user={user} />
                </motion.div>
              ))}
            </div>
          )}
        </AnimatePresence>
      </section>
    </div>
  );
};

export default LandingPage;
