import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { useState } from 'react';
import { AnimatePresence, motion } from 'framer-motion';
import Header from './components/Header';
import LandingPage from './pages/LandingPage';
import Login from './pages/Login';
import Register from './pages/Register';
import Footer from './components/Footer';
import AboutPage from './pages/AboutPage';
import FAQPage from './pages/FAQPage';
import BlogPage from './pages/BlogPage';
import ContactPage  from './pages/ContactPage ';
import MyApplications from './pages/MyApplications';
import AddJob from './pages/AddJob';
import Apply from './pages/Apply';
function App() {
  const [user, setUser] = useState(null); // { id, name, email, role, token }

  return (
    <Router>
      <div className="min-h-screen flex flex-col bg-white">
        <Header user={user} setUser={setUser} />
        <main className="flex-grow container mx-auto px-4 py-8">
          <Routes>
            <Route path="/" element={<LandingPage user={user} />} />
            <Route path="/login" element={<Login setUser={setUser} />} />
            <Route path="/register" element={<Register setUser={setUser} />} />
            <Route path="/about" element={<AboutPage setUser={setUser} />} />
            <Route path="/faq" element={<FAQPage setUser={setUser} />} />
            <Route path="/blog" element={<BlogPage  setUser={setUser} />} />
            <Route path="/contact" element={<ContactPage   setUser={setUser} />} />
            <Route path="/apply/:jobId" element={<Apply user={user} />} />
            <Route path="/my-applications" element={<MyApplications   setUser={setUser} />} />
            <Route path="/add-job" element={<AddJob   setUser={setUser} />} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
