import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { FaFacebook, FaTwitter, FaLinkedin, FaInstagram } from 'react-icons/fa';

const Header = ({ user, setUser }) => {
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    navigate('/');
    setIsMenuOpen(false);
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const linkVariants = {
    hover: { scale: 1.05, color: '#10B981', transition: { duration: 0.2 } },
    tap: { scale: 0.95 },
  };

  const iconVariants = {
    hover: { scale: 1.2, rotate: 10, transition: { duration: 0.2 } },
    tap: { scale: 0.9 },
  };

  return (
    <>
      <motion.header
        initial={{ y: -100, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.6, ease: 'easeOut' }}
        className="fixed w-full top-0 z-50 bg-primary/95 backdrop-blur-md text-white py-4 shadow-2xl rounded-b-2xl"
        style={{
          backgroundImage: `linear-gradient(rgba(30, 64, 175, 0.85), rgba(30, 64, 175, 0.85)), url('https://images.unsplash.com/photo-1516321310762-479437144403?ixlib=rb-4.0.3&auto=format&fit=crop&w=1400&q=80')`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
        }}
      >
        <div className="container mx-auto px-6 flex justify-between items-center">
          {/* Logo */}
          <Link to="/">
            <motion.div
              initial={{ scale: 0.9 }}
              animate={{ scale: 1 }}
              transition={{ duration: 0.5, repeat: Infinity, repeatType: 'reverse', repeatDelay: 1.5 }}
              className="text-2xl md:text-3xl font-extrabold tracking-tight bg-gradient-to-r from-white to-secondary bg-clip-text text-transparent"
            >
              Talent<span className="text-secondary">Hub</span>
            </motion.div>
          </Link>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-8">
            <nav className="flex items-center space-x-6 font-medium">
              {user ? (
                <>
                  <span className="text-sm italic opacity-90">Hi, {user.name}</span>
                  {user.role === 'employer' && (
                    <motion.div variants={linkVariants} whileHover="hover" whileTap="tap">
                      <Link
                        to="/add-job"
                        className="relative text-white hover:text-secondary transition after:content-[''] after:block after:h-[2px] after:w-0 after:bg-secondary after:transition-all after:duration-300 hover:after:w-full"
                      >
                        Add Job
                      </Link>
                    </motion.div>
                  )}
                  {user.role === 'applicant' && (
                    <motion.div variants={linkVariants} whileHover="hover" whileTap="tap">
                      <Link
                        to="/my-applications"
                        className="relative text-white hover:text-secondary transition after:content-[''] after:block after:h-[2px] after:w-0 after:bg-secondary after:transition-all after:duration-300 hover:after:w-full"
                      >
                        My Applications
                      </Link>
                    </motion.div>
                  )}
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={handleLogout}
                    className="bg-secondary text-white px-5 py-2 rounded-full font-semibold shadow-md hover:bg-green-700 transition"
                  >
                    Logout
                  </motion.button>
                </>
              ) : (
                <>
                  <motion.div variants={linkVariants} whileHover="hover" whileTap="tap">
                    <Link
                      to="/login"
                      className="relative text-white hover:text-secondary transition after:content-[''] after:block after:h-[2px] after:w-0 after:bg-secondary after:transition-all after:duration-300 hover:after:w-full"
                    >
                      Login
                    </Link>
                  </motion.div>
                  <motion.a
                    href="/register"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    className="bg-secondary text-white px-5 py-2 rounded-full font-semibold shadow-md hover:bg-green-700 transition"
                  >
                    Register
                  </motion.a>
                </>
              )}
            </nav>
            {/* Social Icons */}
            <div className="flex space-x-3">
              <motion.a href="#" variants={iconVariants} whileHover="hover" whileTap="tap" className="bg-white/10 hover:bg-white/20 p-2 rounded-full transition text-white">
                <FaFacebook size={20} />
              </motion.a>
              <motion.a href="#" variants={iconVariants} whileHover="hover" whileTap="tap" className="bg-white/10 hover:bg-white/20 p-2 rounded-full transition text-white">
                <FaTwitter size={20} />
              </motion.a>
              <motion.a href="#" variants={iconVariants} whileHover="hover" whileTap="tap" className="bg-white/10 hover:bg-white/20 p-2 rounded-full transition text-white">
                <FaLinkedin size={20} />
              </motion.a>
              <motion.a href="#" variants={iconVariants} whileHover="hover" whileTap="tap" className="bg-white/10 hover:bg-white/20 p-2 rounded-full transition text-white">
                <FaInstagram size={20} />
              </motion.a>
            </div>
          </div>

          {/* Mobile Menu Button */}
          <motion.button
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            className="md:hidden text-white focus:outline-none"
            onClick={toggleMenu}
          >
            <svg className="w-7 h-7" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d={isMenuOpen ? 'M6 18L18 6M6 6l12 12' : 'M4 6h16M4 12h16M4 18h16'}
              />
            </svg>
          </motion.button>
        </div>

        {/* Mobile Navigation */}
        <AnimatePresence>
          {isMenuOpen && (
            <motion.div
              initial={{ opacity: 0, x: '100%' }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: '100%' }}
              transition={{ duration: 0.35 }}
              className="md:hidden fixed inset-0 bg-primary/95 backdrop-blur-md shadow-inner z-40 flex flex-col items-center justify-center"
            >
              <nav className="flex flex-col items-center space-y-5 py-6 font-medium">
                {user ? (
                  <>
                    <span className="text-sm italic opacity-90">Hi, {user.name}</span>
                    {user.role === 'employer' && (
                      <Link
                        to="/add-job"
                        className="text-white hover:text-secondary transition"
                        onClick={() => setIsMenuOpen(false)}
                      >
                        Add Job
                      </Link>
                    )}
                    {user.role === 'applicant' && (
                      <Link
                        to="/my-applications"
                        className="text-white hover:text-secondary transition"
                        onClick={() => setIsMenuOpen(false)}
                      >
                        My Applications
                      </Link>
                    )}
                    <motion.button
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      onClick={handleLogout}
                      className="bg-secondary text-white px-5 py-2 rounded-full font-semibold shadow-md hover:bg-green-700 transition"
                    >
                      Logout
                    </motion.button>
                  </>
                ) : (
                  <>
                    <Link
                      to="/login"
                      className="text-white hover:text-secondary transition"
                      onClick={() => setIsMenuOpen(false)}
                    >
                      Login
                    </Link>
                    <motion.a
                      href="/register"
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      className="bg-secondary text-white px-5 py-2 rounded-full font-semibold shadow-md hover:bg-green-700 transition"
                      onClick={() => setIsMenuOpen(false)}
                    >
                      Register
                    </motion.a>
                  </>
                )}
                {/* Social Icons */}
                <div className="flex space-x-4 mt-4">
                  <motion.a href="#" variants={iconVariants} whileHover="hover" whileTap="tap" className="bg-white/10 hover:bg-white/20 p-3 rounded-full transition text-white">
                    <FaFacebook size={24} />
                  </motion.a>
                  <motion.a href="#" variants={iconVariants} whileHover="hover" whileTap="tap" className="bg-white/10 hover:bg-white/20 p-3 rounded-full transition text-white">
                    <FaTwitter size={24} />
                  </motion.a>
                  <motion.a href="#" variants={iconVariants} whileHover="hover" whileTap="tap" className="bg-white/10 hover:bg-white/20 p-3 rounded-full transition text-white">
                    <FaLinkedin size={24} />
                  </motion.a>
                  <motion.a href="#" variants={iconVariants} whileHover="hover" whileTap="tap" className="bg-white/10 hover:bg-white/20 p-3 rounded-full transition text-white">
                    <FaInstagram size={24} />
                  </motion.a>
                </div>
              </nav>
            </motion.div>
          )}
        </AnimatePresence>
        <div className="h-16"></div>
      </motion.header>
    </>
  );
};

export default Header;
