import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const JobCard = ({ job, user }) => {
  // Fallback image if job.image not provided
  const jobImage = job.image || 'https://images.unsplash.com/photo-1516321310762-479437144403?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80';

  return (
    <motion.div
      whileHover={{ scale: 1.03, shadow: '0 10px 20px rgba(0,0,0,0.1)' }}
      className="relative bg-white border border-gray-200 rounded-xl shadow-sm transition-shadow duration-300 overflow-hidden"
    >
      {job.isFeatured && (
        <span className="absolute top-0 left-0 bg-secondary text-white text-xs font-semibold px-3 py-1 rounded-br-lg z-10">
          Featured
        </span>
      )}
      <div className="h-40 w-full overflow-hidden">
        <img
          src={jobImage}
          alt={job.title}
          className="w-full h-full object-cover transition-transform duration-300 hover:scale-105"
        />
      </div>
      <div className="p-4">
        <h3 className="text-xl font-semibold text-primary mb-2">{job.title}</h3>
        <p className="text-gray-600 mb-3 line-clamp-3 text-sm">{job.description}</p>
        <p className="text-xs text-gray-500 mb-1">Posted by: {job.createdBy}</p>
        <p className="text-xs text-gray-500 mb-3">Category: {job.category}</p>
        {user && user.role === 'applicant' && (
          <Link
            to={`/apply/${job.id}`}
            className="inline-block bg-secondary text-white px-4 py-2 rounded-full font-semibold hover:bg-green-700 transition"
          >
            Apply Now
          </Link>
        )}
      </div>
    </motion.div>
  );
};

export default JobCard;
