import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';

const Header = ({ user, setUser }) => {
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    navigate('/');
    setIsMenuOpen(false);
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <motion.header
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ duration: 0.5 }}
      className="backdrop-blur-md bg-primary/95 text-white py-4 fixed w-full top-0 z-50 shadow-lg"
    >
      <div className="container mx-auto px-6 flex justify-between items-center">
        {/* Logo */}
        <Link
          to="/"
          className="text-2xl md:text-3xl font-extrabold tracking-tight relative group"
        >
          Talent<span className="text-secondary">Hub</span>
          <span className="absolute left-0 -bottom-1 w-0 h-[2px] bg-secondary transition-all duration-300 group-hover:w-full"></span>
        </Link>

        {/* Desktop Navigation */}
        <nav className="hidden md:flex items-center space-x-8 font-medium">
          {user ? (
            <>
              <span className="text-sm italic opacity-90">
                Hi, {user.name}
              </span>
              {user.role === 'employer' && (
                <Link
                  to="/add-job"
                  className="relative hover:text-secondary transition-colors after:content-[''] after:block after:h-[2px] after:w-0 after:bg-secondary after:transition-all after:duration-300 hover:after:w-full"
                >
                  Add Job
                </Link>
              )}
              {user.role === 'applicant' && (
                <Link
                  to="/my-applications"
                  className="relative hover:text-secondary transition-colors after:content-[''] after:block after:h-[2px] after:w-0 after:bg-secondary after:transition-all after:duration-300 hover:after:w-full"
                >
                  My Applications
                </Link>
              )}
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={handleLogout}
                className="ml-4 bg-secondary text-white px-5 py-2 rounded-full font-semibold shadow-md hover:bg-green-700 transition"
              >
                Logout
              </motion.button>
            </>
          ) : (
            <>
              <Link
                to="/login"
                className="relative hover:text-secondary transition-colors after:content-[''] after:block after:h-[2px] after:w-0 after:bg-secondary after:transition-all after:duration-300 hover:after:w-full"
              >
                Login
              </Link>
              <motion.a
                href="/register"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className="bg-secondary text-white px-5 py-2 rounded-full font-semibold shadow-md hover:bg-green-700 transition"
              >
                Register
              </motion.a>
            </>
          )}
        </nav>

        {/* Mobile Menu Button */}
        <button
          className="md:hidden text-white focus:outline-none"
          onClick={toggleMenu}
        >
          <svg
            className="w-7 h-7"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d={
                isMenuOpen
                  ? 'M6 18L18 6M6 6l12 12'
                  : 'M4 6h16M4 12h16M4 18h16'
              }
            />
          </svg>
        </button>
      </div>

      {/* Mobile Navigation */}
      <motion.div
        initial={{ height: 0, opacity: 0 }}
        animate={{
          height: isMenuOpen ? 'auto' : 0,
          opacity: isMenuOpen ? 1 : 0,
        }}
        transition={{ duration: 0.35 }}
        className="md:hidden bg-primary/95 backdrop-blur-md overflow-hidden shadow-inner"
      >
        <nav className="flex flex-col items-center space-y-5 py-6 font-medium border-t border-blue-800">
          {user ? (
            <>
              <span className="text-sm italic opacity-90">
                Hi, {user.name}
              </span>
              {user.role === 'employer' && (
                <Link
                  to="/add-job"
                  className="hover:text-secondary transition"
                  onClick={() => setIsMenuOpen(false)}
                >
                  Add Job
                </Link>
              )}
              {user.role === 'applicant' && (
                <Link
                  to="/my-applications"
                  className="hover:text-secondary transition"
                  onClick={() => setIsMenuOpen(false)}
                >
                  My Applications
                </Link>
              )}
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={handleLogout}
                className="bg-secondary text-white px-5 py-2 rounded-full font-semibold shadow-md hover:bg-green-700 transition"
              >
                Logout
              </motion.button>
            </>
          ) : (
            <>
              <Link
                to="/login"
                className="hover:text-secondary transition"
                onClick={() => setIsMenuOpen(false)}
              >
                Login
              </Link>
              <motion.a
                href="/register"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => setIsMenuOpen(false)}
                className="bg-secondary text-white px-5 py-2 rounded-full font-semibold shadow-md hover:bg-green-700 transition"
              >
                Register
              </motion.a>
            </>
          )}
        </nav>
      </motion.div>
    </motion.header>
  );
};

export default Header;
