import { Link, useNavigate } from 'react-router-dom';

const Header = ({ user, setUser }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    navigate('/');
  };

  return (
    <header className="bg-primary text-white py-4">
      <div className="container mx-auto px-4 flex justify-between items-center">
        <Link to="/" className="text-2xl font-bold">TalentHub</Link>
        <nav className="space-x-4">
          {user ? (
            <>
              <span>Welcome, {user.name}</span>
              {user.role === 'employer' && <Link to="/add-job" className="hover:underline">Add Job</Link>}
              {user.role === 'applicant' && <Link to="/my-applications" className="hover:underline">My Applications</Link>}
              <button onClick={handleLogout} className="hover:underline">Logout</button>
            </>
          ) : (
            <>
              <Link to="/login" className="hover:underline">Login</Link>
              <Link to="/register" className="hover:underline">Register</Link>
            </>
          )}
        </nav>
      </div>
    </header>
  );
};

export default Header;
