import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import axios from 'axios';
import JobCard from '../components/JobCard';

const LandingPage = ({ user }) => {
  const [jobs, setJobs] = useState([]);
  const [filteredJobs, setFilteredJobs] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [category, setCategory] = useState('all');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const res = await axios.get('/api/jobs');
        const jobsWithCategories = res.data.map((job, index) => ({
          ...job,
          category: index % 2 === 0 ? 'Software' : 'Design',
          isFeatured: index === 0,
        }));
        setJobs(jobsWithCategories);
        setFilteredJobs(jobsWithCategories);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchJobs();
  }, []);

  useEffect(() => {
    const results = jobs.filter(job =>
      (job.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        job.description.toLowerCase().includes(searchTerm.toLowerCase())) &&
      (category === 'all' || job.category === category)
    );
    setFilteredJobs(results);
  }, [searchTerm, category, jobs]);

  if (loading) return (
    <div className="flex justify-center items-center h-64">
      <div className="animate-spin rounded-full h-12 w-12 border-t-4 border-b-4 border-primary"></div>
    </div>
  );

  return (
    <div className="bg-white space-y-20">
      {/* Hero Section */}
      <motion.section
        initial={{ opacity: 0, y: -40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="relative text-center py-24 bg-cover bg-center rounded-3xl shadow-2xl overflow-hidden"
        style={{
          backgroundImage: `linear-gradient(rgba(30, 64, 175, 0.8), rgba(30, 64, 175, 0.8)), url('https://images.unsplash.com/photo-1516321310762-479437144403?ixlib=rb-4.0.3&auto=format&fit=crop&w=1400&q=80')`,
        }}
      >
        <div className="container mx-auto px-6">
          <h1 className="text-4xl md:text-6xl font-extrabold text-white mb-6 leading-tight">
            TalentHub: <br /> Where Careers Take Off ðŸš€
          </h1>
          <p className="text-lg md:text-2xl text-gray-100 mb-8 max-w-2xl mx-auto">
            Discover exciting opportunities or hire world-class talent in one trusted hub.
          </p>
          {user ? (
            <p className="text-sm text-gray-200 italic">Welcome, {user.name} ({user.role})</p>
          ) : (
            <div className="flex flex-wrap justify-center gap-4">
              <motion.a
                href="/register"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className="inline-block bg-secondary text-white px-8 py-3 rounded-full font-semibold shadow-md hover:shadow-xl transition"
              >
                Join Now
              </motion.a>
              <motion.a
                href="/login"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className="inline-block bg-white text-primary px-8 py-3 rounded-full font-semibold shadow-md hover:bg-gray-100 transition"
              >
                Sign In
              </motion.a>
            </div>
          )}
        </div>
      </motion.section>

      {/* Search and Filter */}
      <section className="bg-gradient-to-r from-blue-50 to-green-50 py-10 rounded-3xl shadow-inner">
        <div className="container mx-auto px-6 flex flex-col sm:flex-row justify-center items-center gap-6">
          <input
            type="text"
            placeholder="ðŸ” Search jobs by title or description..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full max-w-md p-3 border border-gray-300 rounded-full shadow-sm focus:outline-none focus:ring-2 focus:ring-primary transition"
          />
          <select
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            className="w-full max-w-xs p-3 border border-gray-300 rounded-full shadow-sm focus:outline-none focus:ring-2 focus:ring-primary"
          >
            <option value="all">All Categories</option>
            <option value="Software">Software</option>
            <option value="Design">Design</option>
          </select>
        </div>
      </section>

      {/* Job Listings */}
      <section className="container mx-auto px-6">
        <h2 className="text-3xl md:text-4xl font-bold mb-10 text-primary text-center">
          ðŸŒŸ Explore Job Opportunities
        </h2>
        <AnimatePresence>
          {filteredJobs.length === 0 ? (
            <motion.p
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="text-center text-gray-500 text-lg"
            >
              No jobs match your search. Try adjusting your filters.
            </motion.p>
          ) : (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
              {filteredJobs.map((job, index) => (
                <motion.div
                  key={job.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5, delay: index * 0.1 }}
                >
                  <JobCard job={job} user={user} />
                </motion.div>
              ))}
            </div>
          )}
        </AnimatePresence>
      </section>

      {/* Footer */}
      <footer className="bg-primary text-white py-8 mt-16 rounded-t-3xl">
        <div className="container mx-auto px-6 text-center space-y-3">
          <h3 className="text-lg font-semibold">TalentHub</h3>
          <p className="text-sm text-gray-200">
            Â© {new Date().getFullYear()} TalentHub. All rights reserved.
          </p>
        </div>
      </footer>
    </div>
  );
};

export default LandingPage;
