import { useState } from 'react';
import { motion } from 'framer-motion';
import axios from 'axios';
import ContactImage from '../assets/contact.jpg'; // <- Local image

const ContactPage = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/contact', { name, email, subject, message });
      setSuccess('Your message has been sent successfully!');
      setError('');
      setName('');
      setEmail('');
      setSubject('');
      setMessage('');
    } catch (err) {
      setError('Failed to send message. Please try again.');
      setSuccess('');
    }
  };

  return (
    <div className="bg-white px-4 md:px-6 py-16 md:py-20 space-y-16 md:space-y-20">
      {/* Hero Section */}
      <motion.section
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="text-center py-12 md:py-16 bg-gradient-to-r from-blue-600 to-blue-400 rounded-2xl md:rounded-3xl shadow-2xl text-white"
      >
        <h1 className="text-4xl md:text-5xl font-extrabold mb-4 md:mb-6">
          Contact Us
        </h1>
        <p className="text-lg md:text-xl max-w-2xl mx-auto">
          Have questions, suggestions, or need help? Reach out to us using the form below.
        </p>
      </motion.section>

      {/* Contact Form + Image */}
      <section className="max-w-5xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-12 md:gap-16 items-center">
        {/* Form */}
        <motion.div
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
          className="bg-blue-50 rounded-2xl md:rounded-3xl shadow-inner p-8 md:p-12"
        >
          <h2 className="text-2xl md:text-3xl font-bold text-primary mb-6">
            Send Us a Message
          </h2>
          {success && <p className="text-green-600 mb-4">{success}</p>}
          {error && <p className="text-red-600 mb-4">{error}</p>}
          <form onSubmit={handleSubmit} className="space-y-4">
            <input
              type="text"
              placeholder="Your Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="w-full p-3 md:p-4 border border-gray-300 rounded-full shadow-sm focus:outline-none focus:ring-2 focus:ring-primary transition"
              required
            />
            <input
              type="email"
              placeholder="Your Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full p-3 md:p-4 border border-gray-300 rounded-full shadow-sm focus:outline-none focus:ring-2 focus:ring-primary transition"
              required
            />
            <input
              type="text"
              placeholder="Subject"
              value={subject}
              onChange={(e) => setSubject(e.target.value)}
              className="w-full p-3 md:p-4 border border-gray-300 rounded-full shadow-sm focus:outline-none focus:ring-2 focus:ring-primary transition"
              required
            />
            <textarea
              placeholder="Your Message"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              className="w-full p-3 md:p-4 border border-gray-300 rounded-2xl shadow-sm focus:outline-none focus:ring-2 focus:ring-primary transition resize-none h-32 md:h-40"
              required
            />
            <button
              type="submit"
              className="w-full bg-secondary text-white px-6 md:px-8 py-3 md:py-4 rounded-full font-semibold shadow-md hover:bg-green-700 transition"
            >
              Send Message
            </button>
          </form>
        </motion.div>

        {/* Image */}
        <motion.div
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
          className="flex justify-center items-center"
        >
          <img
            src={ContactImage}
            alt="Contact Us"
            className="rounded-2xl md:rounded-3xl shadow-2xl w-full object-cover"
          />
        </motion.div>
      </section>

      {/* Optional Info Section */}
      <section className="max-w-4xl mx-auto text-center space-y-4">
        <h3 className="text-xl md:text-2xl font-semibold text-primary">
          Other Ways to Reach Us
        </h3>
        <p className="text-gray-700">
          Email: support@talenthub.com <br />
          Phone: +1 (555) 123-4567 <br />
          Address: 123 TalentHub Street, City, Country
        </p>
      </section>
    </div>
  );
};

export default ContactPage;
