import { useState, useEffect } from 'react';
import axios from 'axios';
import JobCard from '../components/JobCard';

const LandingPage = ({ user }) => {
  const [jobs, setJobs] = useState([]);
  const [filteredJobs, setFilteredJobs] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const res = await axios.get('/api/jobs');
        setJobs(res.data);
        setFilteredJobs(res.data); // Initialize filtered jobs
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchJobs();
  }, []);

  // Handle search
  useEffect(() => {
    const results = jobs.filter(job =>
      job.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      job.description.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredJobs(results);
  }, [searchTerm, jobs]);

  if (loading) return (
    <div className="flex justify-center items-center h-64">
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary"></div>
    </div>
  );

  return (
    <div className="space-y-12">
      {/* Hero Section */}
      <section className="text-center py-12 bg-gradient-to-r from-primary to-blue-600 text-white rounded-lg shadow-lg">
        <h1 className="text-4xl md:text-5xl font-bold mb-4">Welcome to TalentHub</h1>
        <p className="text-lg md:text-xl mb-6">Discover your next career opportunity or find top talent for your team.</p>
        {user ? (
          <p className="text-sm">Logged in as {user.name} ({user.role})</p>
        ) : (
          <div className="space-x-4">
            <a href="/register" className="inline-block bg-secondary text-white px-6 py-3 rounded-full font-semibold hover:bg-green-700 transition">
              Get Started
            </a>
            <a href="/login" className="inline-block bg-white text-primary px-6 py-3 rounded-full font-semibold hover:bg-gray-100 transition">
              Log In
            </a>
          </div>
        )}
      </section>

      {/* Search Bar */}
      <section className="flex justify-center">
        <input
          type="text"
          placeholder="Search jobs by title or description..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full max-w-lg p-3 border border-gray-300 rounded-full shadow-sm focus:outline-none focus:ring-2 focus:ring-primary"
        />
      </section>

      {/* Job Listings */}
      <section>
        <h2 className="text-3xl font-bold mb-6 text-primary text-center">Available Jobs</h2>
        {filteredJobs.length === 0 ? (
          <p className="text-center text-gray-500">No jobs match your search.</p>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredJobs.map(job => (
              <JobCard key={job.id} job={job} user={user} />
            ))}
          </div>
        )}
      </section>
    </div>
  );
};

export default LandingPage;
