import React,{useState} from 'react';
import axios from 'axios';
function Register(){
const [name,setName]=useState(''); const [email,setEmail]=useState(''); const [password,setPassword]=useState(''); const [role,setRole]=useState('applicant');
const handleSubmit=async(e)=>{e.preventDefault(); try{ const res=await axios.post('http://localhost:5000/auth/register',{name,email,password,role}); console.log(res.data);}catch(err){console.log(err);}};
return <form onSubmit={handleSubmit} className="max-w-md mx-auto mt-10 space-y-2">
<input className="border p-2 w-full" placeholder="Name" value={name} onChange={e=>setName(e.target.value)}/>
<input className="border p-2 w-full" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)}/>
<input type="password" className="border p-2 w-full" placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)}/>
<select className="border p-2 w-full" value={role} onChange={e=>setRole(e.target.value)}>
<option value="applicant">Developer</option>
<option value="employer">Employer</option>
</select>
<button type="submit" className="bg-green-600 text-white px-4 py-2 w-full">Register</button>
</form>;
}
export default Register;
