import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { useState } from 'react';
import { AnimatePresence, motion } from 'framer-motion';
import Header from './components/Header';
import LandingPage from './pages/LandingPage';
import Login from './pages/Login';
import Register from './pages/Register';

function App() {
  const [user, setUser] = useState(null); // { id, name, email, role, token }

  return (
    <Router>
      <div className="min-h-screen flex flex-col bg-gradient-to-br from-white via-gray-50 to-blue-50">

        {/* Sticky Header */}
        <Header user={user} setUser={setUser} />

        {/* Animated Page Wrapper */}
        <main className="flex-grow pt-20 px-4 md:px-8 lg:px-20">
          <AnimatePresence mode="wait">
            <Routes>
              <Route
                path="/"
                element={
                  <motion.div
                    key="home"
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -30 }}
                    transition={{ duration: 0.4 }}
                  >
                    <LandingPage user={user} />
                  </motion.div>
                }
              />
              <Route
                path="/login"
                element={
                  <motion.div
                    key="login"
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -30 }}
                    transition={{ duration: 0.4 }}
                  >
                    <Login setUser={setUser} />
                  </motion.div>
                }
              />
              <Route
                path="/register"
                element={
                  <motion.div
                    key="register"
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -30 }}
                    transition={{ duration: 0.4 }}
                  >
                    <Register setUser={setUser} />
                  </motion.div>
                }
              />
            </Routes>
          </AnimatePresence>
        </main>

        {/* Footer */}
        <footer className="bg-[#064E86] text-white py-6 mt-10 shadow-inner">
          <div className="container mx-auto px-4 text-center">
            <h2 className="text-lg font-semibold tracking-wide">TalentHub</h2>
            <p className="text-sm opacity-80 mt-1">
              Connecting talent with opportunities ✨
            </p>
            <p className="text-xs opacity-60 mt-2">
              © {new Date().getFullYear()} TalentHub. All rights reserved.
            </p>
          </div>
        </footer>
      </div>
    </Router>
  );
}

export default App;
