import { Link } from 'react-router-dom';

const JobCard = ({ job, user, onApply }) => {
  return (
    <div className="bg-white border border-gray-200 rounded-lg p-4 mb-4 shadow-md">
      <h3 className="text-xl font-bold text-primary">{job.title}</h3>
      <p className="text-gray-600">{job.description}</p>
      <p className="text-sm text-gray-500">Posted by: {job.createdBy}</p>
      {user && user.role === 'applicant' && (
        <Link to={`/apply/${job.id}`} className="mt-2 inline-block bg-secondary text-white px-4 py-2 rounded hover:bg-green-700">
          Apply
        </Link>
      )}
    </div>
  );
};

export default JobCard;
