import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { useState } from 'react';
import Header from './components/Header';
import LandingPage from './pages/LandingPage';
import Login from './pages/Login';
import Register from './pages/Register';

function App() {
  const [user, setUser] = useState(null); // { id, name, email, role, token }

  return (
    <Router>
      <div className="min-h-screen flex flex-col bg-white">
        {/* Header (fixed) */}
        <Header user={user} setUser={setUser} />

        {/* Main Content (padding-top = header height) */}
        <main className="flex-grow pt-20">
          <Routes>
            <Route path="/" element={<LandingPage user={user} />} />
            <Route path="/login" element={<Login setUser={setUser} />} />
            <Route path="/register" element={<Register setUser={setUser} />} />
          </Routes>
        </main>

        {/* Footer */}
        <footer className="bg-primary text-white py-6 mt-10">
          <div className="container mx-auto px-4 text-center text-sm">
            <p className="font-semibold">TalentHub</p>
            <p className="text-gray-300">Â© {new Date().getFullYear()} TalentHub. All rights reserved.</p>
          </div>
        </footer>
      </div>
    </Router>
  );
}

export default App;
