import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const JobCard = ({ job, user }) => {
  // Category-specific images (replace with backend-provided URLs if available)
  const jobImage = job.category === 'Software'
    ? 'https://images.unsplash.com/photo-1516321310762-479437144403?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80'
    : 'https://images.unsplash.com/photo-1600585154340-be6161a56a0c?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80';

  return (
    <motion.div
      whileHover={{ scale: 1.03, shadow: '0 10px 20px rgba(0,0,0,0.1)' }}
      className="relative bg-white border border-gray-200 rounded-lg p-6 shadow-sm hover:shadow-xl transition-shadow duration-300"
    >
      {job.isFeatured && (
        <span className="absolute top-0 left-0 bg-secondary text-white text-xs font-semibold px-3 py-1 rounded-br-lg">
          Featured
        </span>
      )}
      <img
        src={jobImage}
        alt={job.title}
        className="w-full h-40 object-cover rounded-t-lg mb-4"
      />
      <h3 className="text-xl font-semibold text-primary mb-2">{job.title}</h3>
      <p className="text-gray-600 mb-3 line-clamp-2 text-sm">{job.description}</p>
      <p className="text-xs text-gray-500 mb-2">Posted by: {job.createdBy}</p>
      <p className="text-xs text-gray-500 mb-4">Category: {job.category}</p>
      {user && user.role === 'applicant' && (
        <Link
          to={`/apply/${job.id}`}
          className="inline-block bg-secondary text-white px-4 py-2 rounded-full font-semibold hover:bg-green-700 transition"
        >
          Apply Now
        </Link>
      )}
    </motion.div>
  );
};

export default JobCard;
