import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast';
import JobCard from '../components/JobCard';

const LandingPage = ({ user }) => {
  const [jobs, setJobs] = useState([]);
  const [filteredJobs, setFilteredJobs] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [category, setCategory] = useState('all');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const res = await axios.get('http://localhost:5000/jobs');
        const jobsWithCategories = res.data.map((job, index) => ({
          ...job,
          category: index % 2 === 0 ? 'Software' : 'Design',
          isFeatured: index === 0,
        }));
        setJobs(jobsWithCategories);
        setFilteredJobs(jobsWithCategories);
      } catch (err) {
        console.error('Fetch jobs error:', err);
        toast.error('Failed to load jobs. Please try again.', {
          icon: '❌',
        });
      } finally {
        setLoading(false);
      }
    };
    fetchJobs();
  }, []);

  useEffect(() => {
    const results = jobs.filter(
      (job) =>
        (job.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
          job.description.toLowerCase().includes(searchTerm.toLowerCase())) &&
        (category === 'all' || job.category === category)
    );
    setFilteredJobs(results);
  }, [searchTerm, category, jobs]);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen bg-gray-50">
        <motion.div
          animate={{ rotate: 360 }}
          transition={{ duration: 1, repeat: Infinity, ease: 'linear' }}
          className="h-12 w-12 border-t-4 border-b-4 border-primary rounded-full"
          aria-label="Loading jobs"
        />
      </div>
    );
  }

  return (
    <div className="bg-gray-50 min-h-screen font-sans">
      {/* Toaster component with custom styling */}
      <Toaster
        position="top-right"
        reverseOrder={false}
        toastOptions={{
          style: {
            maxWidth: '400px',
            padding: '16px',
            borderRadius: '8px',
            boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
            fontSize: '16px',
            fontWeight: '500',
          },
          success: {
            style: {
              background: '#10B981',
              color: '#ffffff',
              border: '1px solid #059669',
            },
            iconTheme: {
              primary: '#ffffff',
              secondary: '#10B981',
            },
          },
          error: {
            style: {
              background: '#EF4444',
              color: '#ffffff',
              border: '1px solid #B91C1C',
            },
            iconTheme: {
              primary: '#ffffff',
              secondary: '#EF4444',
            },
          },
        }}
      />

      {/* Hero Section */}
      <motion.section
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, ease: 'easeOut' }}
        className="relative text-center py-16 md:py-24 bg-gradient-to-br from-blue-600 to-blue-800 rounded-3xl shadow-2xl overflow-hidden mx-4 md:mx-8 mt-8"
        style={{
          backgroundImage: `linear-gradient(135deg, rgba(30, 64, 175, 0.9), rgba(6, 182, 212, 0.7)), url('https://images.unsplash.com/photo-1516321310762-479437144403?ixlib=rb-4.0.3&auto=format&fit=crop&w=1400&q=80')`,
        }}
      >
        <div className="container mx-auto px-4 md:px-8">
          <motion.h1
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.2, duration: 0.6 }}
            className="text-4xl md:text-5xl lg:text-6xl font-extrabold text-white mb-4 md:mb-6 leading-tight tracking-tight"
          >
            TalentHub: Your Career Launchpad 🚀
          </motion.h1>
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.4, duration: 0.6 }}
            className="text-lg md:text-xl lg:text-2xl text-gray-100 mb-8 max-w-3xl mx-auto"
          >
            Connect with top opportunities or hire exceptional talent in a seamless, trusted platform.
          </motion.p>
          {user ? (
            <motion.p
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.6, duration: 0.6 }}
              className="text-base md:text-lg text-gray-200 italic"
            >
              Welcome back, {user.username || user.name} ({user.role})
            </motion.p>
          ) : (
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.6, duration: 0.6 }}
              className="flex flex-col sm:flex-row justify-center gap-4 md:gap-6"
            >
              <motion.a
                href="/register"
                whileHover={{ scale: 1.05, boxShadow: '0 8px 24px rgba(0, 0, 0, 0.2)' }}
                whileTap={{ scale: 0.95 }}
                className="inline-block bg-green-500 text-white px-6 md:px-8 py-3 md:py-4 rounded-full font-semibold text-base md:text-lg shadow-lg hover:bg-green-600 transition duration-300"
                aria-label="Join TalentHub"
              >
                Get Started
              </motion.a>
              <motion.a
                href="/login"
                whileHover={{ scale: 1.05, boxShadow: '0 8px 24px rgba(0, 0, 0, 0.2)' }}
                whileTap={{ scale: 0.95 }}
                className="inline-block bg-white text-blue-600 px-6 md:px-8 py-3 md:py-4 rounded-full font-semibold text-base md:text-lg shadow-lg hover:bg-gray-100 transition duration-300"
                aria-label="Sign in to TalentHub"
              >
                Sign In
              </motion.a>
            </motion.div>
          )}
        </div>
      </motion.section>

      {/* Search + Filter */}
      <section className="bg-gradient-to-r from-blue-50 to-green-50 py-10 md:py-12 rounded-3xl shadow-lg mx-4 md:mx-8">
        <div className="container mx-auto px-4 md:px-8 flex flex-col sm:flex-row justify-center items-center gap-4 md:gap-6">
          <div className="relative w-full sm:max-w-md">
            <input
              type="text"
              placeholder="Search jobs by title or description..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full p-4 pl-12 border border-gray-200 rounded-full shadow-md focus:outline-none focus:ring-2 focus:ring-green-500 transition bg-white text-gray-700 placeholder-gray-400"
              aria-label="Search jobs"
            />
            <svg
              className="absolute left-4 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
            </svg>
          </div>
          <div className="relative w-full sm:max-w-xs">
            <select
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              className="w-full p-4 border border-gray-200 rounded-full shadow-md focus:outline-none focus:ring-2 focus:ring-green-500 transition bg-white text-gray-700 appearance-none pr-10"
              aria-label="Filter jobs by category"
            >
              <option value="all">All Categories</option>
              <option value="Software">Software</option>
              <option value="Design">Design</option>
            </select>
            <svg
              className="absolute right-4 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7" />
            </svg>
          </div>
        </div>
      </section>

      {/* Job Listings */}
      <section className="container mx-auto px-4 md:px-8 py-12">
        <motion.h2
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="text-3xl md:text-4xl font-bold mb-10 text-blue-800 text-center"
        >
          🌟 Discover Your Next Opportunity
        </motion.h2>
        <AnimatePresence>
          {filteredJobs.length === 0 ? (
            <motion.p
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="text-center text-gray-600 text-lg md:text-xl"
            >
              No jobs match your search. Try adjusting your filters.
            </motion.p>
          ) : (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 md:gap-8">
              {filteredJobs.map((job, index) => (
                <motion.div
                  key={job.id}
                  initial={{ opacity: 0, y: 30 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5, delay: index * 0.1 }}
                  className="hover:scale-[1.02] transition-transform duration-300"
                >
                  <JobCard job={job} user={user} />
                </motion.div>
              ))}
            </div>
          )}
        </AnimatePresence>
      </section>
    </div>
  );
};

export default LandingPage;
